import React, { Component,useState } from 'react'
import axios from 'axios';
import ImageGallery from 'react-image-gallery'
import styles from "./photo.css"
import {Row, Form, Button} from "react-bootstrap";
import $ from "jquery";
import {} from "jquery.cookie";
//import photos from "./photo"

const headers = { withCredentials: true };

var photos = [{
  original: 'https://picsum.photos/id/1018/1000/600/',
  thumbnail: 'https://picsum.photos/id/1018/250/150/',
},
{
  original: 'https://picsum.photos/id/1015/1000/600/',
  thumbnail: 'https://picsum.photos/id/1015/250/150/',
},
{
  original: 'https://picsum.photos/id/1019/1000/600/',
  thumbnail: 'https://picsum.photos/id/1019/250/150/',
},];

/* popout the browser and maximize to see more rows! -> */

class ImageUpload extends Component {
  constructor(props){
    super(props);
    this.state = {
      selectedFile: null,
    }

  }



componentWillMount() {

  /* popout the browser and maximize to see more rows! -> */

  axios.get("http://192.249.18.153:80/image/").then(res => {
      console.log(res);
      let url = res.config.url.replace("/image","")
      for(var i in res.data.image) {
          photos.push({orginal:url+res.data.image[i],
          thumbnail: url+res.data.image[i]
          });
      }

      console.log(photos);
      })
  }



  handleFileInput(e){
    this.setState({
      selectedFile : e.target.files[0],
    })
  }

  handlePost(){
    let formData = new FormData;
    const config = {
	    header: {'content-type': 'multipart/form-data'}
    }
    formData.append("file", this.state.selectedFile);
    return axios.post("http://192.249.18.153:80/image/upload", formData,config).then(res => {
    console.log(res)
      alert(res.data.success ? '성공':'오류')
      window.location.reload();
    }).catch(err => {
        console.log(err)
      alert('실패')
    }).then(() => window.location.reload())
  }

  render() {
    const divStyle = {
      marginTop: 20,
      marginRight: 200,
      marginLeft:200 
    };
    return (

      <div style={divStyle}>
        여기에 넣고 싶은 사진을 넣어주세요!
        <Form.Group controlId="formFile" className="mb-3">
          <Form.Label></Form.Label>
          <Form.Control type="file" onChange={e => this.handleFileInput(e)}/>
          <Button type="button" onClick={() => this.handlePost()} size = "sm">사진 업로드</Button>
        </Form.Group>

        <ImageGallery  items={photos} 
        showThumbnails={false}
        showPlayButton={false}
        showNav={false}
        showFullscreenButton={false}/>

      </div>
    )
  }
}

export default ImageUpload;
