import React, { Component,useEffect } from 'react'
import axios from 'axios';
import Gallery from "react-photo-gallery";
//import photos from "./photo"

const headers = { withCredentials: true };

var photos = [

];

/* popout the browser and maximize to see more rows! -> */


class ImageUpload extends Component {
  constructor(props){
    super(props);
    this.state = {
      selectedFile: null,
    }
  }

componentDidMount() {


/* popout the browser and maximize to see more rows! -> */

axios.get("http://192.249.18.153:80/image/").then(res => {
    console.log(res);
    let url = res.config.url.replace("/image","")
    for(var i in res.data.image) {
        photos.push({src:url+res.data.image[i],
        width: 400,
        height: 300
        });
    }

    console.log(photos);
    })
}

componentWillReceiveProps() {


  /* popout the browser and maximize to see more rows! -> */

  axios.get("http://192.249.18.153:80/image/").then(res => {
      console.log(res);
      let url = res.config.url.replace("/image","")
      for(var i in res.data.image) {
          photos.push({src:url+res.data.image[i],
          width: 400,
          height: 300
          });
      }

      console.log(photos);
      })
  }



  handleFileInput(e){
    this.setState({
      selectedFile : e.target.files[0],
    })
  }

  handlePost(){
    let formData = new FormData;
    const config = {
	    header: {'content-type': 'multipart/form-data'}
    }
    formData.append("file", this.state.selectedFile);
    return axios.post("http://192.249.18.153:80/image/upload", formData,config).then(res => {
    console.log(res)
      alert(res.data.success ? '성공':'오류')
      window.location.reload();
    }).catch(err => {
        console.log(err)
      alert('실패')
    })
  }

  render() {
    return (
      <div encType='multipart/form-data'>

        <input type="file" name="file" onChange={e => this.handleFileInput(e)}/>
        <button type="button" onClick={() => this.handlePost()}>사진 업로드</button>
        <Gallery photos={photos} />
      </div>
    )
  }
}

export default ImageUpload;
