import React, { Component } from "react";
import { Navbar, Button, Image } from "react-bootstrap";
import { NavLink } from "react-router-dom";
import axios from "axios";
import $ from "jquery";
import {} from "jquery.cookie";
axios.defaults.withCredentials = true;
const headers = { withCredentials: true };

class Header extends Component {
  state = {
    buttonDisplay: "none"
  };

  componentDidMount() {
    if ($.cookie("login_id")) {
      this.setState({
        buttonDisplay: "block"
      });
    } else {
      this.setState({
        buttonDisplay: "none"
      });
    }
  }

  logout = () => {
    axios
      .get("http://172.10.18.147:80/member/logout", {
        headers
      })
      .then(returnData => {
        if (returnData.data.message) {
          $.removeCookie("login_id"); //쿠키 삭제후
          alert("로그아웃 되었습니다!");
          window.location.href = "/"; //로그아웃하면 초기화면으로 돌아감 (페이지 이동?)
        }
      });
  };
  render() {
    const buttonStyle = {
      margin: "15px 10px 15px 10px",
    };

    const titleStyle ={
      marginLeft: 5,
      fontStyle:'bold'
    }

    return (
      <div>
        <Navbar Navbar bg="light" expand="lg">
          <Container>
          <Navbar.Brand href="/"><strong><big>SCRUM for Mad Camp</big></strong></Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav"/>
          <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="ms-auto">
            <NavLink to={{pathname: "/mypage", query: { _id: $.cookie("login_id")}}}>
              <Button variant="primary" style={buttonStyle}>
                Mypage
              </Button>
            </NavLink>
            <NavLink to="/">
              <Button style={buttonStyle} variant="primary">
                글목록
              </Button>
            </NavLink>
            <NavLink to="/boardWrite">
              <Button style={buttonStyle} variant="primary">
                글쓰기
              </Button>
            </NavLink>
            <NavLink to="/calendar">
              <Button variant="primary" style={buttonStyle}>
                todo
              </Button>
            </NavLink>
            <div><Button onClick={this.logout} variant="secondary" style={buttonStyle}>
              logout
            </Button></div>

            </Nav>
          </Navbar.Collapse>
          </Container>
        </Navbar>
      </div>
    );
  }
}

export default Header;
